INSERT INTO `interaction` VALUES ('46', '请问贵公司大股东是不是失信了？', '按照《上市公司监管指引第 4 号——上市公司实际控制人、股东、关联方、收购人以及上市公司承诺及履行》的规定，变更方案未经股东大会审议通过且承诺到期的，视同超期未履行承诺。', '全景网互动精华', 'http://ircs.p5w.net/ircs/interaction/moreQuestionForGszz.do', '2016-07-04 15:21:05', '7', '0', '46027823', null);
