INSERT INTO `interaction` VALUES ('374', '请问武汉水灾公司有没有人员或财物受损的情况发生？', '谢谢您的关心。进入汛期以来，湖北全省普降大雨，防洪抗灾的形势严峻，公司启动了应急预案，加强了日常巡查工作，日常生产经营未受到相关影响。同时公司还积极行动起来，向灾区捐赠救灾物资，以实际行动支持抗洪救灾工作。', '全景网互动精华', 'http://ircs.p5w.net/ircs/interaction/moreQuestionForGszz.do', '2016-07-05 13:33:04', '7', '0', '3757905191', null);
INSERT INTO `interaction` VALUES ('375', '公司中报高送转何时推出，希望尽早推出！', '您好！公司无应披露而未披露的信息，请关注公司公告，谢谢！', '全景网互动精华', 'http://ircs.p5w.net/ircs/interaction/moreQuestionForGszz.do', '2016-07-05 15:25:53', '7', '0', '2496170307', null);
