INSERT INTO `interaction` VALUES ('915', '建议公司管理层尽快处理好债务纠纷问题，响应国家政策、转变观念，目前血液制品景气度高，对比一下其他几家血液制品公司，公司好好搞一下市值管理，提高公司管理水平，我相信目前的债务对于今后的大股东在资本市场获得的回报，毛毛雨啦', '谢谢您的建议与关注。', '全景网互动精华', 'http://ircs.p5w.net/ircs/interaction/moreQuestionForGszz.do', '2016-07-07 10:45:34', '7', '0', '1321816590', 'ST生化 000403');
