INSERT INTO `interaction` VALUES ('917', '请问公司是否打算扩大对武汉海达数云技术有限公司股权比例？', '你好，我司将严格按照规定及时进行信息披露，敬请关注我司公告。', '全景网互动精华', 'http://ircs.p5w.net/ircs/interaction/moreQuestionForGszz.do', '2016-07-07 10:45:34', '7', '0', '3545554470', '中海达 300177');
