INSERT INTO `news` VALUES ('3630774', null, null, null, '【TD元老真才基出任中国电信副总 或加速4G网融合】7月12日，国资委新闻中心官方微博今日确认，原大唐电信集团董事长真才基近日调任中国电信集团公司党组成员、副总经理，排名列孙康敏之后。真才基现年56岁，2006年6月进入大唐电信集团，历任大唐电信集团董事长、总裁，电信科学技术研究院院长、党组书记，大唐电信科技产业控股有限公司执行董事、总裁。', 'http://www.yicai.com/brief/', null, '第一财经快讯', 'http://www.yicai.com/brief/', null, null, '', null, '2016-07-12 15:43:32', 'normal', 'page', 'TD元老真才基出任中国电信副总或加速4G网融合7月12日国资委新闻中心官方微博今日确认原大唐电信集团董事长真才基近日调任中国电信集团公司党组成员副总经理排名列孙康敏之后真才基现年56岁2006年6月进入大唐电信集团历任大唐电信集团董事长总裁电信科学技术研究院院长党组书记大唐电信科技产业控股有限公司执行董事总裁', '12', null, null, null, '985505089');
