INSERT INTO `news` VALUES ('3630781', null, null, null, '【金种子酒预计2016年上半年业绩与去年同期相比将减少60%-80%】金种子酒公告称，预计2016年上半年净利润与去年同期相比将减少60%-80%，公司去年同期净利润为34,264,916.53元。公告称，公司报告期内业绩变动原因是：因受市场因素影响，公司本年度1-6月营业收入与去年同期相比出现下降，导致净利润出现大幅下降。（WritingMaster）', 'http://www.yicai.com/brief/', null, '第一财经快讯', 'http://www.yicai.com/brief/', null, null, '', null, '2016-07-12 15:54:42', 'normal', 'page', '金种子酒预计2016年上半年业绩与去年同期相比将减少6080金种子酒公告称预计2016年上半年净利润与去年同期相比将减少6080公司去年同期净利润为3426491653元公告称公司报告期内业绩变动原因是因受市场因素影响公司本年度16月营业收入与去年同期相比出现下降导致净利润出现大幅下降WritingMaster', '12', null, null, null, '4116659513');
